import Container from "react-bootstrap/esm/Container";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/esm/Button";
import { useState } from "react";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  return (
    <Container
      className="d-flex justify-content-center align-items-center"
      style={{ height: window.innerHeight - 56 }}
    >
      <Card style={{ width: 600 }} className="p-5">
        <h2 className="m-auto">{isLogin ? "Авторизация" : "Регистрация"}</h2>
        <Form className="d-flex flex-column">
          <Form.Control className="mt-4" placeholder="Введите email" />
          <Form.Control className="mt-4" placeholder="Введите пароль" />
          <Button type="submit" variant="outline-success" className="mt-4">
            {isLogin ? "Войти" : "Зарегистрироваться"}
          </Button>
          <div onClick={() => setIsLogin((prev) => !prev)} className="mt-2">
            {!isLogin ? "Войти" : "Зарегистрироваться"}
          </div>
        </Form>
      </Card>
    </Container>
  );
};

export default AuthPage;
