import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Dropdown from "react-bootstrap/Dropdown";
import { useContext, useState } from "react";
import { userContext } from "../..";

const CreateDevice = ({ onHide }) => {
    const {device} = useContext(userContext)
    const [info, setInfo] = useState([])
  return (
    <Modal show={true} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Добавить устройство</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form style={{display: "flex", flexDirection: "column", gap: 15}}>
          <Dropdown>
            <Dropdown.Toggle variant="outline-dark" id="dropdown-basic">
              Выберите тип устройства
            </Dropdown.Toggle>

            <Dropdown.Menu>
                {device.types.map((item) => <Dropdown.Item key={item.id}>{item.name}</Dropdown.Item>)}
            </Dropdown.Menu>
          </Dropdown>
          <Dropdown>
            <Dropdown.Toggle variant="outline-dark" id="dropdown-basic">
              Выберите бренд
            </Dropdown.Toggle>

            <Dropdown.Menu>
                {device.brands.map((item) => <Dropdown.Item key={item.id}>{item.name}</Dropdown.Item>)}
            </Dropdown.Menu>
          </Dropdown>
          <Form.Control  type="text" placeholder="Название устройства" />
          <Form.Control type="number" placeholder="Цена устройства" />
          <Form.Control type="file" placeholder="Изображение устройства" />
          <hr />
          <Button variant="outline-info">Добавить новое свойство</Button>
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Закрыть
        </Button>
        <Button variant="primary" onClick={onHide}>
          Сохранить
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateDevice;
