import Button from "react-bootstrap/esm/Button";
import Col from "react-bootstrap/esm/Col";
import Container from "react-bootstrap/esm/Container";
import Row from "react-bootstrap/esm/Row";
import Image from "react-bootstrap/Image";

const DevicePage = () => {
  const device = {
    id: 1,
    name: "Iphone 16 PRO MAX",
    price: 120000,
    rating: 5,
    img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
  };
  const deviceInfo = [
    {
      id: 1,
      title: "Оперативная память",
      description: "5 ГБ",
    },
    {
      id: 2,
      title: "Камера",
      description: "12 МП",
    },
    {
      id: 3,
      title: "Процессор",
      description: "Snapdragon",
    },
    {
      id: 4,
      title: "Кол-во ядер",
      description: "3",
    },
    {
      id: 5,
      title: "Аккумулятор",
      description: "12000",
    },
  ];

  return (
    <Container className="mt-2">
      <Row>
        <Col md={4}>
          <Image src={device.img} width={300} height={300} />
        </Col>
        <Col md={4}>
          <h2>{device.name}</h2>
          <p>Цена: {device.price}</p>
          <p>Рейтинг: {device.rating}</p>
        </Col>
        <Col md={4}>
          <Button variant="outline-dark">Добавить в корзину</Button>
        </Col>
      </Row>
      <Row>
        {deviceInfo.map((info) => 
        <Row key={info.id}>
          {info.title}: {info.description}
        </Row>)}
      </Row>
    </Container>
  );
};

export default DevicePage;
