import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Dropdown from "react-bootstrap/Dropdown";
import { useContext, useState } from "react";
import { userContext } from "../..";

const CreateDevice = ({ onHide }) => {
  const { device } = useContext(userContext);
  const [name, setName] = useState("");
  const [price, setPrice] = useState(0);
  const [file, setFile] = useState(null);
  const [info, setInfo] = useState(null);

  const addInfo = () => {
    setInfo((prev) => [
      ...prev,
      { title: "", description: "", number: Date.now() },
    ]);
  };

  const removeInfo = (number) => {
    setInfo((prev) => prev.filter((i) => i.number !== number));
  };

  const selectFile = (e) => {
    setFile(e.target.files[0]);
  };

  return (
    <Modal show={true} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Добавить устройство</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form style={{ display: "flex", flexDirection: "column", gap: 15 }}>
          <Dropdown>
            <Dropdown.Toggle variant="outline-dark" id="dropdown-basic">
              Выберите тип устройства
            </Dropdown.Toggle>

            <Dropdown.Menu>
              {device.types.map((item) => (
                <Dropdown.Item
                  key={item.id}
                  onClick={() => device.setSelectedType(item)}
                >
                  {item.name}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
          <Dropdown>
            <Dropdown.Toggle variant="outline-dark" id="dropdown-basic">
              Выберите бренд
            </Dropdown.Toggle>

            <Dropdown.Menu>
              {device.brands.map((item) => (
                <Dropdown.Item
                  key={item.id}
                  onClick={() => device.setSelectedBrand(item)}
                >
                  {item.name}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
          <Form.Control
            type="text"
            placeholder="Название устройства"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Form.Control
            type="number"
            placeholder="Цена устройства"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <Form.Control
            type="file"
            placeholder="Изображение устройства"
            value={file}
            onChange={selectFile}
          />
          <hr />
          <Button variant="outline-secondary" onClick={addInfo}>
            Добавить новое свойство
          </Button>
          {info.map((item) => (
            <div
              key={item.number}
              style={{
                marginTop: 15,
                display: "flex",
                flexDirection: "column",
                gap: 15,
                padding: 10,
                border: "1px solid gray",
                borderRadius: 8,
              }}
            >
              <Form.Control placeholder="Введите название характеристики" />
              <Form.Control placeholder="Введите описание характеристики" />
              <Button
                variant="outline-danger"
                onClick={() => removeInfo(item.number)}
              >
                Удалить свойство
              </Button>
            </div>
          ))}
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Закрыть
        </Button>
        <Button variant="primary" onClick={onHide}>
          Сохранить
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateDevice;
