import Button from "react-bootstrap/esm/Button";
import Col from "react-bootstrap/esm/Col";
import Container from "react-bootstrap/esm/Container";
import Row from "react-bootstrap/esm/Row";
import Image from "react-bootstrap/Image";

const DevicePage = () => {
  const device = {
    id: 1,
    name: "Iphone 16 PRO MAX",
    price: 120000,
    rating: 5,
    img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
  };

  return (
    <Container className="mt-2">
      <Col md={4}>
      <Image src={device.img} width={300} height={300}/>
      </Col>
      <Col md={4}>
      <Row>
        <h2>{device.name}</h2>
        <p>Цена: {device.price}</p>
        <p>Рейтинг: {device.rating}</p>
        </Row>
        </Col>
      <Col md={4}><Button variant="outline-dark">Добавить в корзину</Button></Col>
    </Container>
  );
};

export default DevicePage;
