import Col from "react-bootstrap/esm/Col";
import Card from "react-bootstrap/Card";
import Image from "react-bootstrap/Image";

const DeviceItem = ({device}) => {
  return <Col md={3}>
    <Card className="p-2" style={{width: '100%', height: 320, cursor: 'pointer'}}>
        <Image src={device.img} width={150} height={150}/>
        <div style={{display: 'flex', flexDirection: "column", gap: 15}}>
            <h4 style={{margin: 0}}>{device.name}</h4>
            <p style={{margin: 0}}>Цена: {device.price} рублей</p>
            <p style={{margin: 0}}>Рейтинг: {device.rating}</p>
        </div>
    </Card>
  </Col>;
};

export default DeviceItem;
