import { makeAutoObservable } from "mobx";

export default class DeviceStore {
  constructor() {
    this._types = [
      { id: 1, name: "Холодильники" },
      { id: 2, name: "Смартфоны" },
      { id: 3, name: "Телевизоры" },
      { id: 4, name: "Ноутбуки" },
    ];
    this._brands = [
      { id: 1, name: "Samsung" },
      { id: 2, name: "Apple" },
    ];
    this._devices = [
      {
        id: 1,
        name: "Iphone 16 PRO MAX",
        price: 120000,
        rating: 5,
        img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
      },
      {
        id: 2,
        name: "Iphone 16",
        price: 100000,
        rating: 5,
        img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
      },
      {
        id: 3,
        name: "Iphone 15 PRO MAX",
        price: 900000,
        rating: 4,
        img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
      },
      {
        id: 4,
        name: "Iphone 15",
        price: 85000,
        rating: 4,
        img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
      },
      {
        id: 5,
        name: "Iphone 14",
        price: 71000,
        rating: 3,
        img: "https://mobilworld.by/upload/iblock/aeb/aeb0954e0845ff9ffba5df3e107f57d7.png",
      },
    ];
    makeAutoObservable(this);
  }

  setTypes(types) {
    this._types = types;
  }
  get types() {
    return this._types;
  }
  setBrands(brands) {
    this._brands = brands;
  }
  get brands() {
    return this._brands;
  }
  setDevices(devices) {
    this._devices = devices;
  }
  get devices() {
    return this._devices;
  }
}
